</template>

<script>
import Meeting from "@/API/Meeting/Meeting";
import FileActionsModal from "@/components/commen/FileActionsModal.vue";
import Auth from "@/API/Auth";

export default {
  components: {
    FileActionsModal,
  },
  setup() {
    const meetingApi = new Meeting();
    return { meetingApi };
  },
  props: {
    meetingData: {
      type: Object,
      required: true,
    },
    authUser: {
      type: Object,
      default: () => ({}),
    },
    users: {
      type: Array,
      default: () => [],
    },
  },
  mounted() {
    this.selectedFiles = this.getValidFiles(this.meetingData?.meeting_files);
  },
  computed: {
    canChangeStatus() {
      const canChange = this.meetingData?.participants?.some(
        (p) =>
          p.id === Auth.USER.id &&
          (p.type === "chairperson" ||
            p.type === "vice_chair" ||
            p.type === "secretary")
      );

      return canChange;
    },
    meetingFilesCount() {
      if (
        !this.selectedFiles ||
        (this.selectedFiles.length === 1 &&
          (this.selectedFiles[0] === null ||
            this.getFileNameFromPath(this.selectedFiles[0]) ===
              "meeting_files"))
      ) {
        return 0;
      }
      return this.selectedFiles.length;
    },
  },
  data() {
    return {
      showModalDescriptionOfCategory: false,
      fullDescription: "",
      showModalListDialog: false,
      modalListType: "",
      modalListTitle: "",
      selectedFiles: [],
      isFileActionLoading: false,
      showFileModal: false,
      loadingFileId: null,
      selectedMeeting: {},
      selectedStatus: this.meetingData?.status || "upcoming",
      isStatusUpdating: false,
      statusOptions: [
        { value: "upcoming", label: this.$t("meeting.upcoming") },
        { value: "ongoing", label: this.$t("meeting.ongoing") },
        { value: "completed", label: this.$t("meeting.completed") },
        { value: "cancelled", label: this.$t("meeting.cancelled") },
      ],
    };
  },
  watch: {
    "meetingData.status"(newStatus) {
      this.selectedStatus = newStatus;
    },
  },
  methods: {
    formatDate(date) {
      if (!date) return this.$t('common.na');
      const d = new Date(date);
      const day = d.getDate().toString().padStart(2, "0");
      const month = (d.getMonth() + 1).toString().padStart(2, "0");
      const year = d.getFullYear();
      const hours = d.getHours() % 12 || 12;
      const minutes = d.getMinutes().toString().padStart(2, "0");
      const ampm = d.getHours() >= 12 ? "PM" : "AM";
      return `${day}/${month}/${year} ${hours}:${minutes} ${ampm}`;
    },

    truncateText(text, length = 50) {
      if (!text) return "";
      return text.length > length ? text.substring(0, length) + "..." : text;
    },

    openModalDescriptionOfCategory(description) {
      this.fullDescription = description;
      this.showModalDescriptionOfCategory = true;
    },

    closeModalDescriptionOfCategory() {
      this.showModalDescriptionOfCategory = false;
      this.fullDescription = "";
    },

    stripHtml(html) {
      const div = document.createElement("div");
      div.innerHTML = html;
      return div.textContent || div.innerText || "";
    },

    getStatusLabel(status) {
      const option = this.statusOptions.find((opt) => opt.value === status);
      return option ? option.label : status;
    },

    updateStatus(newStatus) {
      this.selectedStatus = newStatus;
      this.handleStatusChange();
    },

    async handleStatusChange() {
      let response = null;
      console.log("this.meetingData.isVoteStarted +++++++++  ++++", this.meetingData.isVoteStarted);
      if (
        !this.meetingData.isVoteEnded &&
        this.selectedStatus == "completed"
      ) {
        this.meetingApi.poup(
          null,
          "Vote has been started, you can not change status to completed until vote is closed"
        );
        this.selectedStatus = this.meetingData?.status;
        return;
      }
      try {
        this.isStatusUpdating = true;
        const updatedMeeting = { ...this.meetingData };
        updatedMeeting.status = this.selectedStatus;

        response = await this.meetingApi.changeStatus(this.meetingData.id, {
          status: this.selectedStatus,
        });

        if (response?.status === "success") {
          this.meetingApi.poup(response, "Status Updated");
          updatedMeeting.status = this.selectedStatus;
          this.meetingData.status = this.selectedStatus;
        } else {
          this.meetingApi.poup(null, response);
        }
      } catch (error) {
        console.error("Error updating status:", error);
        this.selectedStatus = this.meetingData?.status;
      } finally {
        this.isStatusUpdating = false;
        this.selectedStatus = this.meetingData?.status;
      }
    },

    openFilesModal(meeting) {
      this.selectedMeeting = { ...meeting };
      this.selectedFiles = this.getValidFiles(meeting.meeting_files);
      this.showFileModal = true;
    },

    getValidFiles(files) {
      if (!files || files.length === 0) return [];
      if (
        files.length === 1 &&
        this.getFileNameFromPath(files[0]) === "meeting_files"
      )
        return [];
      return files || [];
    },

    async handleFileUpload(file) {
      try {
        this.isFileActionLoading = true;
        const updatedMeeting = { ...this.meetingData };

        if (!updatedMeeting.meeting_files) {
          updatedMeeting.meeting_files = [];
        }

        updatedMeeting.meeting_files.push(file);
        const response = await this.meetingApi.update(updatedMeeting);

        if (response.status === "success") {
          this.$emit("update:meetingData", response.data);
          Object.assign(this.meetingData, response.data);
          this.meetingApi.poup(response, this.$t("meeting.fileUploaded"));
          this.selectedFiles = this.getValidFiles(response.data.meeting_files);
        }
      } catch (error) {
        console.error("Error uploading file:", error);
        this.meetingApi.poup(null, this.$t("meeting.fileUploadFailed"));
      } finally {
        this.isFileActionLoading = false;
      }
    },

    async handleFileEdit(file) {
      try {
        this.loadingFileId = file.oldFile;
        const updatedMeeting = { ...this.meetingData };

        updatedMeeting.meeting_files = updatedMeeting.meeting_files.map((f) =>
          f === file.oldFile ? file.newFile : f
        );

        const response = await this.meetingApi.update(updatedMeeting);

        if (response.status === "success") {
          this.$emit("update:meetingData", response.data);
          Object.assign(this.meetingData, response.data);
          this.meetingApi.poup(response, this.$t("meeting.fileUpdated"));
          this.selectedFiles = this.getValidFiles(response.data.meeting_files);
        }
      } catch (error) {
        console.error("Error updating file:", error);
        this.meetingApi.poup(null, this.$t("meeting.fileUpdateFailed"));
      } finally {
        this.loadingFileId = null;
      }
    },

    async handleFileDelete(fileData) {
      try {
        this.loadingFileId = fileData.file;
        const updatedMeeting = { ...this.meetingData };

        updatedMeeting.meeting_files = updatedMeeting.meeting_files.filter(
          (f) => f !== fileData.file
        );

        updatedMeeting.meeting_files = updatedMeeting.meeting_files.map(
          (fileUrl) => this.getFileNameFromPath(fileUrl)
        );

        const response = await this.meetingApi.update(updatedMeeting);

        if (response.status === "success") {
          this.$emit("update:meetingData", response.data);
          Object.assign(this.meetingData, response.data);
          this.meetingApi.poup(response, this.$t("meeting.fileDeleted"));
          this.selectedFiles = this.getValidFiles(response.data.meeting_files);
        }
      } catch (error) {
        console.error("Error deleting file:", error);
        this.meetingApi.poup(null, this.$t("meeting.fileDeleteFailed"));
      } finally {
        this.loadingFileId = null;
      }
    },

    getFileNameFromPath(url) {
      return url?.split("/").pop() || "";
    },

    getFileName(file) {
      if (!file) return "";
      if (file.name) return file.name;
      if (file.filePath) return file.filePath.split("/").pop();
      if (typeof file === "string") return file.split("/").pop();
      return this.$t('common.unknown');
    },
  },
};
</script>

